import React, { useState, useEffect } from 'react';
import io from "socket.io-client";

//const socket = io('http://localhost:5000'); // Replace with your server URL
var socket;
const ENDPOINT = "http://localhost:5000";
var socket = io(ENDPOINT);
const ChatApp = () => {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const room = 'your-room-name'; // Replace with the room name you want to join

    useEffect(() => {
        socket.on('receiveMessage', (message) => {
            setMessages((prevMessages) => [...prevMessages, message]);
        });

        // Join the specified room on component mount
        socket.emit('joinRoom', room);

        return () => {
            // Leave the room when the component unmounts
            socket.emit('leaveRoom', room);
        };
    }, [room]);

    const sendMessage = () => {
        if (inputMessage.trim() !== '') {
            const newMessage = {
                user: 'User', // Replace this with the actual user name or ID
                content: inputMessage,
            };
            socket.emit('sendMessage', newMessage, room);
            setInputMessage('');
        }
    };

    const hahdleview = () => {
        setview(true)
    }

    const [view, setview] = useState(false)

    return (
        <>
            <div>

            <input
                            type="text"
                            value={inputMessage}
                            onChange={(e) => setInputMessage(e.target.value)}
                        />
                        <button onClick={sendMessage}>Send</button>
                <button onClick={hahdleview}>Join Room</button>
            </div>

            {view ? (
                <div>
                    <div>
                        {messages.map((message, index) => (
                            <div key={index}>
                                <strong>{message.user}:</strong> {message.content}
                            </div>
                        ))}
                    </div>
                    <div>
                        <input
                            type="text"
                            value={inputMessage}
                            onChange={(e) => setInputMessage(e.target.value)}
                        />
                        <button onClick={sendMessage}>Send</button>
                    </div>
                </div>

            ) : (<></>)}
        </>
    );
};

export default ChatApp;
