import React, { useState, useEffect } from 'react';
import io from "socket.io-client";

//const socket = io('http://localhost:5000'); // Replace with your server URL
var socket;
const ENDPOINT = "http://localhost:5000";
var socket = io.connect(ENDPOINT);
const ChatApp = () => {
    const [messages, setMessages] = useState([]);
    const [inputMessage, setInputMessage] = useState('');
    const room = 'Chat Group'; // Replace with the room name you want to join

    

    const sendMessage = async () => {
        if (inputMessage.trim() !== '') {
            const newMessage = {
                user: name, // Replace this with the actual user name or ID
                content: inputMessage,
            };
            await socket.emit('sendMessage', newMessage, room, name);
            //setMessages((prevMessages) => [...prevMessages, newMessage]);
            setInputMessage('');
        }
    };

    const hahdleview = async () => {
        if (name != "") {
            await socket.emit('joinRoom', name, room);
            setview(true)
        }
        return
    }

    const [view, setview] = useState(false)
    const [name, setname] = useState("");
    const [h1Elements, setH1Elements] = useState([]);

    // const handleButtonClick = () => {
    //     setH1Elements((prevElements) => [...prevElements, <h1 key={prevElements.length}>Hello, this is an h1 element!</h1>]);
    // };


    return (
        <>
            <div>

                <input
                    type="text"
                    value={name}
                    onChange={(e) => setname(e.target.value)}
                />
                {/* <button onClick={sendMessage}>Send</button> */}
                <button onClick={hahdleview}>Join Room</button>
            </div>

            {view ? (
                <div>
                    <h3>{room}</h3>

                    {h1Elements.map((h1Element, index) => (
                        <div key={index}>{h1Element}</div>
                    ))}
                    <div>
                        {messages.map((message, index) => (
                            <div key={index}>
                                <strong>{message.user}:</strong> {message.content}
                            </div>
                        ))}
                    </div>
                    <div>
                        <input
                            type="text"
                            value={inputMessage}
                            onChange={(e) => setInputMessage(e.target.value)}
                        />
                        <button onClick={sendMessage}>Send</button>
                    </div>
                </div>

            ) : (<></>)}
        </>
    );
};

export default ChatApp;
